SELECT prestamo.numPrestamo, libro.Titulo, socio.DNI,socio.Nombre, socio.Apellido, carrera.nombreCarrera, prestamo.Plazo, prestamo.Fecha,date_add(Fecha,INTERVAL Plazo DAY),estado.Estado FROM prestamo INNER JOIN libro ON prestamo.Libro_idlibro = libro.idlibro JOIN socio ON prestamo.Socio_idSocio = socio.idSocio JOIN estado ON prestamo.Estado_idEstado = estado.idEstado JOIN carrera ON socio.Carrera_idCarrera = carrera.idCarrera;